package Lecture2;

public class GradeCard {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks = 90;
		if (marks > 90) {
			System.out.println("Grade A");
		} else if (marks > 80) {
			System.out.println("Grade B");
		} else if (marks > 70) {
			System.out.println("Grade C");
		} else {
			System.out.println("Grade F");
		}
	}

}
